if (PropertiesTestStep.getPropertyValue("total").toString() == "") PropertiesTestStep.setPropertyValue("total", Sheet1.getRows().toString()) count++ //Read the Excel test data Cell Field1 = Sheet1.getCell(0, count) Cell Field2 = Sheet1.getCell(1, count)
log.info("Count is " + count.toString() + " EmpDetails : " + Field1.getContents() + " Empid: " + Field2.getContents())
//Close workbook. WorkBook1.close() //Copy the Excel test data to properties in Properties test step
//Copy the Excel test data to properties in Properties test step PropertiesTestStep.setPropertyValue("EmpDetails", Field1.getContents()) PropertiesTestStep.setPropertyValue("Empid", Field2.getContents()) PropertiesTestStep.setPropertyValue("counter", count.toString()) // check the counter value is less than total no of rows. If same set prop 'end' to 'true' if (count == PropertiesTestStep.getPropertyValue("total").toInteger() - 1) PropertiesTestStep.setPropertyValue("end", "true")

